changeSet.sql:
CREATE FUNCTION chat_greater_than_two_users() RETURNS BOOLEAN AS ' BEGIN RETURN ( SELECT EXISTS(SELECT count(u.login) as nb_users, c.name, c.chat_type as type FROM skh_user u INNER JOIN chat_member cm on u.id = cm.user_id INNER JOIN chat c on c.id = cm.chat_id WHERE c.chat_type = ''GROUPE'' GROUP BY c.id, type HAVING count(u.login) < 3 ORDER BY c.id) ); END; ' LANGUAGE plpgsql;
CREATE FUNCTION chat_two_users() RETURNS BOOLEAN AS ' BEGIN RETURN ( SELECT EXISTS(SELECT count(u.login) as nb_users, c.name, c.chat_type as type FROM skh_user u INNER JOIN chat_member cm on u.id = cm.user_id INNER JOIN chat c on c.id = cm.chat_id WHERE c.chat_type = ''TWO_USER'' GROUP BY c.id, type HAVING count(u.login) <> 2 ORDER BY c.id) ); END; ' LANGUAGE plpgsql;
alter table chat add constraint chat_two_users CHECK (chat_two_users() = false); alter table chat add constraint chat_greater_than_two_users CHECK (chat_greater_than_two_users() = false);
